/**
 * Renders filler word tags in the UI
 * @param {Array} fillerWords - Array of filler word strings
 */
function renderRemoveSilencesWordTags(fillerWords) {
    $('#remove-filler-words-tags').empty();

    const tags = [];
    let html;
    const tagElements = [];

    for (let i = 0; i < fillerWords.length; i++) {
        const word = fillerWords[i];
        const tagHtml = `
      <div
        data-tag-index="${i}"
        class="group relative select-none text-xs text-white rounded bg-shorts-300 px-4 flex items-center"
      >
        <span class="group-hover:-translate-x-[10px]">
          ${word}
        </span>
        <button class="filler-words-add-tag-delete hidden group-hover:block absolute right-0 rounded-sm border-none bg-red-600 px-1.5 py-0 text-xs font-light text-white outline-none hover:border-gray-400 hover:bg-red-400 active:border-red-400 active:bg-red-400">
          x
        </button>
      </div>
    `;
        tagElements.push(tagHtml);
    }

    html = `
    <div class="flex flex-wrap gap-1">
      <input id="filler-words-add-tag-input" class="basis-full hidden grow rounded-md text-white bg-shorts-400 px-2 flex items-center" type="text" />
      <div
        class="filler-words-add-tag select-none text-xs rounded-md text-white  bg-purple-700 hover:bg-purple-600 active:bg-purple-800 px-4 flex items-center cursor-pointer"
      >
        +
      </div>
      ${tagElements.join('')}
    </div>
  `;

    $('#remove-filler-words-tags').prepend(html);
    setFillerWordsListeners();
}

/**
 * Sets up event listeners for filler word tag interactions
 */
function setFillerWordsListeners() {
    // Add tag button click handler
    $('.filler-words-add-tag').on('click', (event) => {
        $('.filler-words-add-tag').addClass('hidden');
        $('#filler-words-add-tag-input').removeClass('hidden');
        $('#filler-words-add-tag-input').focus();

        // Enter key handler for adding new tag
        $('#filler-words-add-tag-input').on('keydown', (e) => {
            if (e.key === 'Enter') {
                e.preventDefault();
                e.stopPropagation();

                const value = $(e.target).val().trim();

                if (!value || value.length === 0) {
                    return;
                }

                $('#filler-words-add-tag-input').off('keydown');
                g_filler_words.push(value);
                writeHTML_filler_words_results();
                renderRemoveSilencesWordTags(g_filler_words);
            }
        });

        // Focus out handler - cancel if empty
        $('#filler-words-add-tag-input').on('focusout', (e) => {
            const value = $(e.target).val().trim();

            if (!value || value.length === 0) {
                renderRemoveSilencesWordTags(g_filler_words);
            }
        });
    });

    // Delete tag button click handler
    $('.filler-words-add-tag-delete').on('click', (event) => {
        const tagIndex = $(event.target).parent('div').attr('data-tag-index');
        const word = g_filler_words[tagIndex];

        filler_words_word_replace_states(word, false, g_filler_words_user_selected);
        filler_words_word_replace_states(word, false, g_filler_words_tags_selected);

        g_filler_words.splice(tagIndex, 1);
        writeHTML_filler_words_results();
        renderRemoveSilencesWordTags(g_filler_words);
    });
}

/**
 * Gets filler words detection parameters from UI settings
 * @returns {Object} Parameters object with scope, language, tracks, etc.
 */
async function test_54_get_filler_words_params() {
    let params = {};
    console.log('test_54');

    try {
        // Initialize key audio track indices array
        params.keyAudioTrackIndices = [];

        // Get checked audio tracks
        $('#setting-remove-filler-words-tracks input').each(function(index) {
            console.log($(this));
            if ($(this).is(':checked')) {
                params.keyAudioTrackIndices.push(parseInt($(this).val()));
                console.log('Added checked track to filler_words_params.keyAudioTrackIndices', parseInt($(this).val()));
            }
        });

        // Get scope setting (in/out points vs full sequence)
        if ($('#remove-filler-words-scope-inout').is(':checked')) {
            params.scope = 'inout';
        } else {
            params.scope = 'full';
        }

        // Get language setting
        params.language = 'en';
        params.language = $('[name=\'setting-remove-filler-words-language\']').val();

        // Get prompt setting
        params.prompt = 1;

        $('[name=\'remove-filler-words-prompt\']').each(function() {
            if ($(this).is(':checked')) {
                params.prompt = parseInt($(this).val());
            }
        });

        // Validate prompt
        if (isNaN(params.prompt)) {
            params.prompt = 1;
        }

        // Get temperature setting
        params.temperature = parseFloat($('#range-remove-filler-words-temperature').val()) / 100;

        // Validate temperature
        if (isNaN(params.temperature)) {
            params.temperature = 0.3;
        }

        if (params.temperature < 0 || params.temperature > 1) {
            params.temperature = 0.3;
        }

        // Get timestamping setting
        params.timestamping = 'prompt';

        $('[name=\'remove-filler-words-timestamping\']').each(function() {
            if ($(this).is(':checked')) {
                params.timestamping = $(this).val();
            }
        });

    } catch (error) {
        console.log('error while getting filler words parameters', error);
    }

    // Get in/out points based on scope
    if (params.scope == 'inout') {
        params.inPoint = await jsxProject.getInPoint();
        params.outPoint = await jsxProject.getOutPoint();
    } else {
        params.inPoint = await jsxProject.getWorkAreaInPoint();
        params.outPoint = await jsxProject.getWorkAreaOutPoint();
    }

    // Ensure inPoint is not negative
    params.inPoint = Math.max(params.inPoint, 0);

    // Validate scope
    params.scopeValid = true;

    if (params.scope == 'inout') {
        // Check if in/out points are equal
        if (params.inPoint == params.outPoint) {
            params.scopeValid = false;
        }
        // Check for negative values
        if (params.inPoint < 0 || params.outPoint < 0) {
            params.scopeValid = false;
        }
        // Check if inPoint is after outPoint
        if (params.inPoint > params.outPoint) {
            params.scopeValid = false;
        }
    }

    console.log(params);
    return params;
}

