 * Writes HTML for filler words detection results
 * Renders each word as a clickable button that can be toggled for deletion
 */
function writeHTML_filler_words_results() {
    let promptSkipCount = 0;
    let html = '';

    for (let chunkIndex = 0; chunkIndex < g_filler_words_transcript.length; chunkIndex++) {
        if (!g_filler_words_transcript[chunkIndex]) {
            continue;
        }

        // If word-level transcript is null, show in italic
        let italicClass = (g_filler_words_transcript_word_level[chunkIndex] === null) ? 'italic' : '';

        html += '<div class="rounded-lg flex flex-wrap">';

        for (let segmentIndex = 0; segmentIndex < g_filler_words_transcript[chunkIndex].segments.length; segmentIndex++) {
            let segment = g_filler_words_transcript[chunkIndex].segments[segmentIndex];

            for (let wordIndex = 0; wordIndex < segment.words.length; wordIndex++) {
                // Check if this is part of the filler prompt to skip
                let wordsToCheck = segment.words.slice(wordIndex, wordIndex + filler_prompt_array.length).join(' ');

                if (wordsToCheck.toLowerCase() === filler_prompt.toLowerCase()) {
                    promptSkipCount = filler_prompt_array.length;
                }

                if (promptSkipCount > 0) {
                    promptSkipCount--;
                    continue;
                }

                let word = segment.words[wordIndex];

                // Handle word object vs string
                if (typeof word === 'object') {
                    word = word.word;
                }

                const isFillerWord = filler_words_is_word_at_transcript_position(chunkIndex, segmentIndex, wordIndex);
                const actionAttribute = isFillerWord ? 'data-action="delete"' : 'data-action="none"';

                html += `<button chunk-index='${chunkIndex}' segment-index='${segmentIndex}' word-index='${wordIndex}' word='${word}' class='${italicClass} text-sm/snug h-5 active:bg-shorts-300 hover:bg-shorts-400 outline-none my-0 py-0 me-0 px-0.5 border-none rounded-sm data-[action=delete]:text-red-400 data-[action=delete]:line-through ' ${actionAttribute}>${word}</button>`;

                // Update chunk status if filler word found and still processing
                if (isFillerWord && g_filler_words_chunks_status[chunkIndex] === 'received from openai') {
                    g_filler_words_chunks_status[chunkIndex] = 'queued for sending to word level timestamps API';
                }
            }
        }

        html += '</div>';

        // Add separator between chunks
        if (chunkIndex < g_filler_words_transcript.length - 1) {
            html += '<hr class="border-shorts-400">';
        }
    }

    $('#remove-filler-words-results').html(html);
    set_filler_words_listeners();
    filler_words_upgrade_accuracy_if_required();
}

