/**
 * Gets filler words detection parameters from UI settings
 * @returns {Object} Parameters object with scope, language, tracks, etc.
 */
async function test_54_get_filler_words_params() {
    let params = {};
    console.log('test_54');

    try {
        // Initialize key audio track indices array
        params.keyAudioTrackIndices = [];

        // Get checked audio tracks
        $('#setting-remove-filler-words-tracks input').each(function(index) {
            console.log($(this));
            if ($(this).is(':checked')) {
                params.keyAudioTrackIndices.push(parseInt($(this).val()));
                console.log('Added checked track to filler_words_params.keyAudioTrackIndices', parseInt($(this).val()));
            }
        });

        // Get scope setting (in/out points vs full sequence)
        if ($('#remove-filler-words-scope-inout').is(':checked')) {
            params.scope = 'inout';
        } else {
            params.scope = 'full';
        }

        // Get language setting
        params.language = 'en';
        params.language = $('[name=\'setting-remove-filler-words-language\']').val();

        // Get prompt setting
        params.prompt = 1;

        $('[name=\'remove-filler-words-prompt\']').each(function() {
            if ($(this).is(':checked')) {
                params.prompt = parseInt($(this).val());
            }
        });

        // Validate prompt
        if (isNaN(params.prompt)) {
            params.prompt = 1;
        }

        // Get temperature setting
        params.temperature = parseFloat($('#range-remove-filler-words-temperature').val()) / 100;

        // Validate temperature
        if (isNaN(params.temperature)) {
            params.temperature = 0.3;
        }

        if (params.temperature < 0 || params.temperature > 1) {
            params.temperature = 0.3;
        }

        // Get timestamping setting
        params.timestamping = 'prompt';

        $('[name=\'remove-filler-words-timestamping\']').each(function() {
            if ($(this).is(':checked')) {
                params.timestamping = $(this).val();
            }
        });

    } catch (error) {
        console.log('error while getting filler words parameters', error);
    }

    // Get in/out points based on scope
    if (params.scope == 'inout') {
        params.inPoint = await jsxProject.getInPoint();
        params.outPoint = await jsxProject.getOutPoint();
    } else {
        params.inPoint = await jsxProject.getWorkAreaInPoint();
        params.outPoint = await jsxProject.getWorkAreaOutPoint();
    }

    // Ensure inPoint is not negative
    params.inPoint = Math.max(params.inPoint, 0);

    // Validate scope
    params.scopeValid = true;

    if (params.scope == 'inout') {
        // Check if in/out points are equal
        if (params.inPoint == params.outPoint) {
            params.scopeValid = false;
        }
        // Check for negative values
        if (params.inPoint < 0 || params.outPoint < 0) {
            params.scopeValid = false;
        }
        // Check if inPoint is after outPoint
        if (params.inPoint > params.outPoint) {
            params.scopeValid = false;
        }
    }

    console.log(params);
    return params;
}

