{
	"name": "komplete-kontrol",
	"displayName": "%extension.displayName%",
	"description": "%extension.description%",
	"publisher": "KompleteKontrol",
	"version": "3.38.1",
	"icon": "assets/icons/icon.png",
	"galleryBanner": {
		"color": "#617A91",
		"theme": "dark"
	},
	"engines": {
		"vscode": "^1.84.0",
		"node": "20.19.2"
	},
	"author": {
		"name": "KOMPLETE-KONTROL"
	},
	"repository": {
		"type": "git",
		"url": "https://github.com/imorgado/KOMPLETE-KONTROL"
	},
	"homepage": "https://github.com/imorgado/KOMPLETE-KONTROL",
	"categories": [
		"AI",
		"Chat",
		"Programming Languages",
		"Education",
		"Snippets",
		"Testing"
	],
	"keywords": [
		"komplete",
		"kontrol",
		"komplete-kontrol",
		"claude",
		"dev",
		"mcp",
		"openrouter",
		"coding",
		"agent",
		"autonomous",
		"chatgpt",
		"sonnet",
		"ai",
		"llama",
		"uncensored",
		"runpod"
	],
	"activationEvents": [
		"onLanguage",
		"onStartupFinished"
	],
	"main": "./dist/extension.js",
	"contributes": {
		"viewsContainers": {
			"activitybar": [
				{
					"id": "roo-cline-ActivityBar",
					"title": "%views.activitybar.title%",
					"icon": "assets/icons/icon.svg"
				}
			]
		},
		"views": {
			"roo-cline-ActivityBar": [
				{
					"type": "webview",
					"id": "roo-cline.SidebarProvider",
					"name": "%views.sidebar.name%"
				}
			]
		},
		"commands": [
			{
				"command": "roo-cline.plusButtonClicked",
				"title": "%command.newTask.title%",
				"icon": "$(edit)"
			},
			{
				"command": "roo-cline.historyButtonClicked",
				"title": "%command.history.title%",
				"icon": "$(history)"
			},
			{
				"command": "roo-cline.marketplaceButtonClicked",
				"title": "%command.marketplace.title%",
				"icon": "$(extensions)"
			},
			{
				"command": "roo-cline.popoutButtonClicked",
				"title": "%command.openInEditor.title%",
				"icon": "$(link-external)"
			},
			{
				"command": "roo-cline.cloudButtonClicked",
				"title": "%command.cloud.title%",
				"icon": "$(cloud)"
			},
			{
				"command": "roo-cline.settingsButtonClicked",
				"title": "%command.settings.title%",
				"icon": "$(settings-gear)"
			},
			{
				"command": "roo-cline.openInNewTab",
				"title": "%command.openInNewTab.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.explainCode",
				"title": "%command.explainCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.fixCode",
				"title": "%command.fixCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.improveCode",
				"title": "%command.improveCode.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.addToContext",
				"title": "%command.addToContext.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.newTask",
				"title": "%command.newTask.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.terminalAddToContext",
				"title": "%command.terminal.addToContext.title%",
				"category": "Terminal"
			},
			{
				"command": "roo-cline.terminalFixCommand",
				"title": "%command.terminal.fixCommand.title%",
				"category": "Terminal"
			},
			{
				"command": "roo-cline.terminalExplainCommand",
				"title": "%command.terminal.explainCommand.title%",
				"category": "Terminal"
			},
			{
				"command": "roo-cline.setCustomStoragePath",
				"title": "%command.setCustomStoragePath.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.importSettings",
				"title": "%command.importSettings.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.focusInput",
				"title": "%command.focusInput.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.acceptInput",
				"title": "%command.acceptInput.title%",
				"category": "%configuration.title%"
			},
			{
				"command": "roo-cline.toggleAutoApprove",
				"title": "%command.toggleAutoApprove.title%",
				"category": "%configuration.title%"
			}
		],
		"menus": {
			"editor/context": [
				{
					"submenu": "roo-cline.contextMenu",
					"group": "1"
				}
			],
			"roo-cline.contextMenu": [
				{
					"command": "roo-cline.addToContext",
					"group": "1_actions@1"
				},
				{
					"command": "roo-cline.explainCode",
					"group": "1_actions@2"
				},
				{
					"command": "roo-cline.improveCode",
					"group": "1_actions@3"
				}
			],
			"terminal/context": [
				{
					"submenu": "roo-cline.terminalMenu",
					"group": "2"
				}
			],
			"roo-cline.terminalMenu": [
				{
					"command": "roo-cline.terminalAddToContext",
					"group": "1_actions@1"
				},
				{
					"command": "roo-cline.terminalFixCommand",
					"group": "1_actions@2"
				},
				{
					"command": "roo-cline.terminalExplainCommand",
					"group": "1_actions@3"
				}
			],
			"view/title": [
				{
					"command": "roo-cline.plusButtonClicked",
					"group": "navigation@1",
					"when": "view == roo-cline.SidebarProvider"
				},
				{
					"command": "roo-cline.settingsButtonClicked",
					"group": "navigation@2",
					"when": "view == roo-cline.SidebarProvider"
				},
				{
					"command": "roo-cline.cloudButtonClicked",
					"group": "navigation@3",
					"when": "view == roo-cline.SidebarProvider"
				},
				{
					"command": "roo-cline.marketplaceButtonClicked",
					"group": "navigation@4",
					"when": "view == roo-cline.SidebarProvider"
				},
				{
					"command": "roo-cline.historyButtonClicked",
					"group": "overflow@1",
					"when": "view == roo-cline.SidebarProvider"
				},
				{
					"command": "roo-cline.popoutButtonClicked",
					"group": "overflow@2",
					"when": "view == roo-cline.SidebarProvider"
				}
			],
			"editor/title": [
				{
					"command": "roo-cline.plusButtonClicked",
					"group": "navigation@1",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				},
				{
					"command": "roo-cline.settingsButtonClicked",
					"group": "navigation@2",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				},
				{
					"command": "roo-cline.cloudButtonClicked",
					"group": "navigation@3",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				},
				{
					"command": "roo-cline.marketplaceButtonClicked",
					"group": "navigation@4",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				},
				{
					"command": "roo-cline.historyButtonClicked",
					"group": "overflow@1",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				},
				{
					"command": "roo-cline.popoutButtonClicked",
					"group": "overflow@2",
					"when": "activeWebviewPanelId == roo-cline.TabPanelProvider"
				}
			]
		},
		"keybindings": [
			{
				"command": "roo-cline.addToContext",
				"key": "cmd+k cmd+a",
				"mac": "cmd+k cmd+a",
				"win": "ctrl+k ctrl+a",
				"linux": "ctrl+k ctrl+a",
				"when": "editorTextFocus && editorHasSelection"
			},
			{
				"command": "roo-cline.toggleAutoApprove",
				"key": "cmd+alt+a",
				"mac": "cmd+alt+a",
				"win": "ctrl+alt+a",
				"linux": "ctrl+alt+a"
			}
		],
		"submenus": [
			{
				"id": "roo-cline.contextMenu",
				"label": "%views.contextMenu.label%"
			},
			{
				"id": "roo-cline.terminalMenu",
				"label": "%views.terminalMenu.label%"
			}
		],
		"configuration": {
			"title": "%configuration.title%",
			"properties": {
				"roo-cline.allowedCommands": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [
						"git log",
						"git diff",
						"git show"
					],
					"description": "%commands.allowedCommands.description%"
				},
				"roo-cline.deniedCommands": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [],
					"description": "%commands.deniedCommands.description%"
				},
				"roo-cline.commandExecutionTimeout": {
					"type": "number",
					"default": 0,
					"minimum": 0,
					"maximum": 600,
					"description": "%commands.commandExecutionTimeout.description%"
				},
				"roo-cline.commandTimeoutAllowlist": {
					"type": "array",
					"items": {
						"type": "string"
					},
					"default": [],
					"description": "%commands.commandTimeoutAllowlist.description%"
				},
				"roo-cline.preventCompletionWithOpenTodos": {
					"type": "boolean",
					"default": false,
					"description": "%commands.preventCompletionWithOpenTodos.description%"
				},
				"roo-cline.vsCodeLmModelSelector": {
					"type": "object",
					"properties": {
						"vendor": {
							"type": "string",
							"description": "%settings.vsCodeLmModelSelector.vendor.description%"
						},
						"family": {
							"type": "string",
							"description": "%settings.vsCodeLmModelSelector.family.description%"
						}
					},
					"description": "%settings.vsCodeLmModelSelector.description%"
				},
				"roo-cline.customStoragePath": {
					"type": "string",
					"default": "",
					"description": "%settings.customStoragePath.description%"
				},
				"roo-cline.enableCodeActions": {
					"type": "boolean",
					"default": true,
					"description": "%settings.enableCodeActions.description%"
				},
				"roo-cline.autoImportSettingsPath": {
					"type": "string",
					"default": "",
					"description": "%settings.autoImportSettingsPath.description%"
				},
				"roo-cline.maximumIndexedFilesForFileSearch": {
					"type": "number",
					"default": 10000,
					"minimum": 5000,
					"maximum": 500000,
					"description": "%settings.maximumIndexedFilesForFileSearch.description%"
				},
				"roo-cline.useAgentRules": {
					"type": "boolean",
					"default": true,
					"description": "%settings.useAgentRules.description%"
				},
				"roo-cline.apiRequestTimeout": {
					"type": "number",
					"default": 600,
					"minimum": 0,
					"maximum": 3600,
					"description": "%settings.apiRequestTimeout.description%"
				},
				"roo-cline.newTaskRequireTodos": {
					"type": "boolean",
					"default": false,
					"description": "%settings.newTaskRequireTodos.description%"
				},
				"roo-cline.codeIndex.embeddingBatchSize": {
					"type": "number",
					"default": 60,
					"minimum": 1,
					"maximum": 200,
					"description": "%settings.codeIndex.embeddingBatchSize.description%"
				},
				"roo-cline.debug": {
					"type": "boolean",
					"default": false,
					"description": "%settings.debug.description%"
				}
			}
		}
	},
	"scripts": {
		"lint": "eslint . --ext=ts --max-warnings=0",
		"check-types": "tsc --noEmit",
		"pretest": "turbo run bundle --cwd ..",
		"test": "vitest run",
		"format": "prettier --write .",
		"bundle": "node esbuild.mjs",
		"vscode:prepublish": "pnpm bundle --production",
		"vsix": "mkdirp ../bin && vsce package --no-dependencies --out ../bin",
		"publish:marketplace": "vsce publish --no-dependencies && ovsx publish --no-dependencies",
		"watch:bundle": "pnpm bundle --watch",
		"watch:tsc": "cd .. && tsc --noEmit --watch --project src/tsconfig.json",
		"clean": "rimraf README.md CHANGELOG.md LICENSE dist logs mock .turbo"
	},
	"dependencies": {
		"@anthropic-ai/bedrock-sdk": "^0.10.2",
		"@anthropic-ai/sdk": "^0.37.0",
		"@anthropic-ai/vertex-sdk": "^0.7.0",
		"@aws-sdk/client-bedrock-runtime": "^3.922.0",
		"@aws-sdk/credential-providers": "^3.922.0",
		"@google/genai": "^1.29.1",
		"@lmstudio/sdk": "^1.1.1",
		"@mistralai/mistralai": "^1.9.18",
		"@modelcontextprotocol/sdk": "1.12.0",
		"@qdrant/js-client-rest": "^1.14.0",
		"@roo-code/cloud": "workspace:^",
		"@roo-code/core": "workspace:^",
		"@roo-code/ipc": "workspace:^",
		"@roo-code/telemetry": "workspace:^",
		"@roo-code/types": "workspace:^",
		"@vscode/codicons": "^0.0.36",
		"async-mutex": "^0.5.0",
		"axios": "^1.12.0",
		"cheerio": "^1.0.0",
		"chokidar": "^4.0.1",
		"clone-deep": "^4.0.1",
		"default-shell": "^2.2.0",
		"delay": "^6.0.0",
		"diff": "^5.2.0",
		"diff-match-patch": "^1.0.5",
		"exceljs": "^4.4.0",
		"fast-deep-equal": "^3.1.3",
		"fast-xml-parser": "^5.0.0",
		"fastest-levenshtein": "^1.0.16",
		"fzf": "^0.5.2",
		"get-folder-size": "^5.0.0",
		"google-auth-library": "^9.15.1",
